import React, { Component } from 'react';
import './styles.css';
import company_logo from '../images/company_logo.jpg';
import RadioButton from './RadioButton';
import TextInput from './TextInput';

export class Registration extends Component {

  constructor(props) {
    super(props);
    this.state = { forecasts: [], userType: "customer" };
  }

  state = {
    selectedValue: 'company',
    name: '',
  };

  handleChange = (event) => {
    this.setState({ name: event.target.value });
  }

  setSelectedValue = (value) => {
    this.setState({ selectedValue: value });
  }

  static renderCompanyFields() {
    return (
        <div>

        </div>
    );
  }

  static renderCustomerFields() {
    return (
        <div className='user-details box-container'>
            <form>
            <TextInput name="title" value={this.state.name} label="Title:" onChange={this.handleChange} />
            <TextInput name="fname" value={this.state.name} label="First name:" onChange={this.handleChange} />
            <TextInput name="lname" value={this.state.name} label="Last name:" onChange={this.handleChange} />
            </form>
        </div>
    );
  }


  render() {
    let content = this.state.userType === "company"
    ? Registration.renderCompanyFields()
    : Registration.renderCustomerFields();

    return(
    <div>
         <img className='company_logo' src={company_logo} alt="words"/>
        <div className='page-title'>
            <h2>Registration</h2>
        </div>
        <div className='registration-type box-container'>
            <form>
            <RadioButton name="company" value="company" label="Company:" selectedValue={this.state.selectedValue} setSelectedValue={this.setSelectedValue} />
            <RadioButton name="customer" value="customer" label="Customer:" selectedValue={this.state.selectedValue} setSelectedValue={this.setSelectedValue} />
            </form>
        </div>
        {content}
    </div>
    )
  }
}
