import React from 'react';
import { NavLink } from 'react-router-dom';
import './styles.css'
import TextInput from './TextInput';

export default class LoginModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      username: '',
      password: '',
    };
  }

  handleButtonClick = () => {
    window.location.reload();
  }
  handleChange = (event) => {
    switch(event.target.id)
    {
      case 'username':
        this.setState({ username: event.target.value });
      break;
      case 'password':
        this.setState({ password: event.target.value });
      break;
    }
  }

  render() {
    return (
      <div>
          <div className="modal-overlay">
            <div className="modal-content">
              <h2>Login</h2>
              <form>
                <TextInput name="username" value={this.state.username} label="Username" onChange={this.handleChange} type="Text"/>
                <TextInput name="password" value={this.state.password} label="Password" onChange={this.handleChange} type="Password"/>
              </form>
              <div>
              <button className='login-back-btn text-dark' onClick={this.handleButtonClick}>Submit
                </button>
                <button className='login-back-btn' onClick={this.handleButtonClick}>
                  <NavLink className="text-dark" to="/">Back</NavLink>
                </button>
              </div>
            </div>
          </div>
      </div>
    );
  }
}
