import React, { Component } from 'react';
import './styles.css';
import company_logo from '../images/company_logo.jpg';

export class Registration extends Component {
  static displayName = Registration.name;

  constructor(props) {
    super(props);
    this.state = { forecasts: [], userType: "customer" };
  }

  static renderCompanyFields() {
    return (
        <div>

        </div>
    );
  }

  static renderCustomerFields() {
    return (
        <div className='user-details box-container'>
            <form>
                <label>
                    Title: 
                    <input type="text" name="name" />
                </label>
                <label>
                    First name: 
                    <input type="text" name="name" />
                </label>
                <label>
                    Last name: 
                    <input type="text" name="name" />
                </label>
            </form>
        </div>
    );
  }


  render() {
    let content = this.state.userType === "company"
    ? Registration.renderCompanyFields()
    : Registration.renderCustomerFields();

    return(
    <div>
         <img className='company_logo' src={company_logo}/>
        <div className='page-title'>
            <h2>Registration</h2>
        </div>
        <div className='registration-type box-container'>
            <form>
                <div className="radio">
                    <label>
                        <input type="radio" value="option1" checked={true} />
                        Company
                    </label>
                </div>
                <div className="radio">
                    <label>
                        <input type="radio" value="option2"/>
                        Customer
                    </label>
                </div>
            </form>
        </div>
        {content}
    </div>
    )
  }
}
