import React, {useState} from 'react';

const RadioSelect = (props) => {
    const { name, value, label, checked } = props;
    const [setChecked] = useState(props.checked);

    const handleChange = (event) => {
        console.log(event.target.value + checked);
        setChecked(event.target.value === value);
  }

  return (
    <div>
    {label}
      <input
        type="radio"
        name={name}
        value={value}
        checked={checked}
        onChange={handleChange}
      />
    </div>
  );
}

export default RadioSelect;
