import React, { Component } from 'react';
import './styles.css';
import company_logo from '../images/company_logo.jpg';

export class Registration extends Component {
  static displayName = Registration.name;

  constructor(props) {
    super(props);
    this.state = { forecasts: [], userType: "customer" };
  }

  static renderCompanyFields() {
    return (
        <div>

        </div>
    );
  }

  static renderCustomerFields() {
    return (
        <div>

        </div>
    );
  }


  render() {
    let content = this.state.userType === "company"
    ? Registration.renderCompanyFields
    : Registration.renderCustomerFields

    return(
    <div>
         <img className='company_logo' src={company_logo}/>
        <div className='page-title'>
            <h2>Registration</h2>
        </div>
        <div className='registration-type box-container'>
            <p>Company</p>
            <p>Customer</p>
        </div>
        {content}
    </div>
    )
  }
}
