import React, { Component } from 'react';
import './styles.css';
import company_logo from '../images/company_logo.jpg';
import RadioButton from './RadioButton';
import TextInput from './TextInput';

export class Registration extends Component {

  constructor(props) {
    super(props);
    this.state = { 
      forecasts: [], 
      userType: "customer",
      selectedValue: 'company',
      title: '',
      fname: '',
      lname: '',

    };
  }

  handleChange = (event) => {
    switch(event.target.id)
    {
        case 'title':
          this.setState({ title: event.target.value });
        break;
        case 'fname':
          this.setState({ fname: event.target.value });
        break;
        case 'lname':
          this.setState({ lname: event.target.value });
        break;
    }
  }

  setSelectedValue = (value) => {
    this.setState({ selectedValue: value });
  }

  renderCompanyFields() {
    return (
        <div>
          <h1> here </h1>
        </div>
    );
  }

  sharedFields() {
    return (
      <div>
            <TextInput name="title" value={this.state.title} label="Title:" onChange={this.handleChange} />
            <TextInput name="fname" value={this.state.fname} label="First name:" onChange={this.handleChange} />
            <TextInput name="lname" value={this.state.lname} label="Last name:" onChange={this.handleChange} />
      </div>    
    )
  }

  renderCustomerFields() {
    return (
        <div className='user-details box-container'>
            <form>
              {this.sharedFields()}
            </form>
        </div>
    );
  }


  render() {
    let content = this.state.userType;
    return(
    <div>
         <img className='company_logo' src={company_logo} alt="words"/>
        <div className='page-title'>
            <h2>Registration</h2>
        </div>
        <div className='registration-type box-container'>
            <form>
            <RadioButton name="company" value="company" label="Company:" selectedValue={this.state.selectedValue} setSelectedValue={this.setSelectedValue} />
            <RadioButton name="customer" value="customer" label="Customer:" selectedValue={this.state.selectedValue} setSelectedValue={this.setSelectedValue} />
            </form>
        </div>
        {content === "company" ? this.renderCompanyFields() : this.renderCustomerFields()}
    </div>
    )
  }
}
