import React, { Component, useState } from 'react';
import './styles.css';
import company_logo from '../images/company_logo.jpg';
import RadioButton from './RadioButton';

export class Registration extends Component {
  static displayName = Registration.name;

  constructor(props) {
    super(props);
    this.state = { forecasts: [], userType: "customer" };
  }

  state = {
    selectedValue: 'company',
  };

  setSelectedValue = (value) => {
    this.setState({ selectedValue: value });
  }

  static renderCompanyFields() {
    return (
        <div>

        </div>
    );
  }

  static renderCustomerFields() {
    return (
        <div className='user-details box-container'>
            <form>
                <label>
                    Title: 
                    <input type="text" name="name" />
                </label>
                <label>
                    First name: 
                    <input type="text" name="name" />
                </label>
                <label>
                    Last name: 
                    <input type="text" name="name" />
                </label>
            </form>
        </div>
    );
  }


  render() {
    let content = this.state.userType === "company"
    ? Registration.renderCompanyFields()
    : Registration.renderCustomerFields();

    return(
    <div>
         <img className='company_logo' src={company_logo}/>
        <div className='page-title'>
            <h2>Registration</h2>
        </div>
        <div className='registration-type box-container'>
            <form>
                <RadioButton name="Company" value="company" label="Company:" selectedValue={selectedValue} setSelectedValue={setSelectedValue} />
                <RadioButton name="Customer" value="customer" label="Customer:" selectedValue={selectedValue} setSelectedValue={setSelectedValue} />
            </form>
        </div>
        {content}
    </div>
    )
  }
}
